import React, { useState } from 'react';
import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';
import './Loginsignup.css';

const LoginSignup = () => {
    const [action, setAction] = useState("LogIn");
    const [formData, setFormData] = useState({
      name: '',
      email: '',
      password: '',
    });


const handleInputChange = (e) => {
  const { name, value } = e.target;
  setFormData((prevData) => ({
    ...prevData,
    [name]: value,
  }));
};

const handleSubmit = async () => {
try {
  const response = await fetch('/api/auth', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(formData),
  });

  if (response.ok) {
    // Authentication successful, handle the response (e.g., store token)
    const data = await response.json();
    console.log('Authentication successful:', data);
  } else {
    // Authentication failed, handle the error
    console.error('Authentication failed');
  }
} catch (error) {
  console.error('Error during authentication:', error);
}
};


  return (
    <div className="container">
      <div className="header">
        <div className="text"> {action} </div>
        <div className="underline"> </div>
      </div>
      <div className="inputs">
        {action ==="LogIn"? <div></div>:   <div className="input">
          <img src={user_icon} alt="" />
          <input
              type="text"
              name="name"
              value={formData.name}
              placeholder="Name"
              onChange={handleInputChange}
            />
        </div>}

        <div className="input">
          <img src={email_icon} alt="" />
          <input type="email"  placeholder="Email"/>
        </div>
        <div className="input">
          <img src={password_icon} alt="" />
          <input type="password"  placeholder="Password" />
        </div>
      </div>
{action=== "SignUp"?<div> </div>:<div className="forgot-password">
        Forgot Password?
      </div>}


      <div className="submit-container">
      <div className={action === "LogIn" ? "submit gray" : "submit"} onClick={() => setAction("SignUp")}>
  Sign Up
</div>
<div className={action === "SignUp" ? "submit gray" : "submit"} onClick={() => setAction("LogIn")}>
  LogIn
</div>
      </div>
    </div>
  );
}

export default LoginSignup;
