import React from 'react';
import { useState } from 'react';

const DataDisplayComponent = ({ dataArray }) => {
  console.log(dataArray);

  // Function to safely format domain_links, ensuring it handles both array and non-array values
  const formatDomainLinks = (domainLinks) => {
    // Check if domainLinks is an array and process accordingly; otherwise, return a default value or as is
    return Array.isArray(domainLinks)
      ? domainLinks.map((link, linkIndex) => (
          <div key={linkIndex}>
            <a href={link} target="_blank" rel="noopener noreferrer">
              {link}
            </a>
          </div>
        ))
      : <div><a href={domainLinks} target="_blank" rel="noopener noreferrer">{domainLinks || 'N/A'}</a></div>;
  };

  const [expandedRows, setExpandedRows] = useState(new Set());

  const handleRowClick = (id) => {
    setExpandedRows((prevExpandedRows) => {
      const newExpandedRows = new Set(prevExpandedRows);
      if (newExpandedRows.has(id)) {
        newExpandedRows.delete(id);
      } else {
        newExpandedRows.add(id);
      }
      return newExpandedRows;
    });
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Filename</th>
          {/* <th>Headlines</th> */}
          <th>Sentiment</th>
          <th>Keywords</th>
          <th>Score</th>
          <th>Upload Date</th>
          <th>Domain Links</th>
        </tr>
      </thead>
      <tbody>
        {dataArray.map((item, index) => (
          <tr key={index} onClick={() => handleRowClick(item._id)}>
            <td>{item.filename}</td>
            {/* <td>{item.headlines}</td> */}
            <td>{item.sentiment}</td>
            <td>{Array.isArray(item.keywords) ? item.keywords.join(', ') : item.keywords || 'N/A'}</td>
            <td>{item.score}</td>
            <td>{item.upload_date}</td>
            <td>
              {formatDomainLinks(item.domain_links)}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default DataDisplayComponent;
