return (
  <button type="button" {...getRootProps()}>
   upload file
   <button variant="contained" startIcon={<handleUpload />}>
  Add item
</button>
   <input type="file" onChange={handleFileChange} />
   <button onClick={handleUpload}>Upload</button>
  </button>

);

  return (
    <div>
      <h2>News Coverage Analysis </h2>

      <h2>Upload File:</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
    </div>
  );
}

export default App;



