import React, { useState } from 'react';
import { useButton } from '@mui/base/useButton';
import styled from 'styled-components';

function App() {
  const [file, setFile] = useState(null);
  const { getRootProps } = useButton();

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = () => {
    const formData = new FormData();
    formData.append('file', file);

    fetch('/upload', {
      method: 'POST',
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        // Handle the response as needed
      })
      .catch(error => console.error('There was an error uploading the file:', error));
  };

  return (
    <Container>
      <Header>News Coverage Analysis</Header>
      <UploadSection>
        <UploadButton {...getRootProps()}>
          Upload File
          <UploadIcon />
        </UploadButton>
        <FileInput type="file" onChange={handleFileChange} />
        <StyledButton onClick={handleUpload}>Upload</StyledButton>
      </UploadSection>
    </Container>
  );
}

// Styled Components
const Container = styled.div`
  text-align: center;
`;

const Header = styled.h2`
  color: #BF4F74;
`;

const UploadSection = styled.div`
  margin-top: 20px;
`;

const UploadButton = styled.button`
  background: #BF4F74;
  border: none;
  border-radius: 5px;
  color: white;
  padding: 10px 20px;
  cursor: pointer;
  transition: background 0.3s ease;

  &:hover {
    background: #A43B5B;
  }

  svg {
    margin-left: 10px;
  }
`;

const FileInput = styled.input`
  display: block;
  margin: 10px auto;
`;

const StyledButton = styled.button`
  background: #BF4F74;
  border: none;
  border-radius: 5px;
  color: white;
  padding: 10px 20px;
  cursor: pointer;
  transition: background 0.3s ease;

  &:hover {
    background: #A43B5B;
  }
`;

const UploadIcon = styled.span`
  display: inline-block;
  width: 20px;
  height: 20px;
  margin-left: 5px;
  background: url('path-to-your-icon.svg') center/cover no-repeat;
`;

export default App;




