  return (

    <Container maxWidth="sm">
      <Typography variant="h2" gutterBottom>
        News Coverage Analysis
      </Typography>

      <h2>Upload File:</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
    //</Container>

  );
}

export default App;



