import React, { useState } from 'react';
import styled from 'styled-components';

const StyledTable = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const StyledTableHeader = styled.th`
  background-color: #f9f9f9;
  border: 1px solid #ddd;
  padding: 8px;
  text-align: left;
`;

const StyledTableRow = styled.tr`
  cursor: pointer;
  &:hover {
    background-color: #f2f2f2;
  }
`;

const StyledTableCell = styled.td`
  border: 1px solid #ddd;
  padding: 8px;
  white-space: nowrap; /* Prevents content from wrapping */
  overflow: hidden; /* Hide overflowing content */
  text-overflow: ellipsis; /* Display ellipsis for overflowed content */
`;

const DataDisplayComponent = ({ data }) => {
  const [expanded, setExpanded] = useState(false);

  const toggleExpansion = () => {
    setExpanded(!expanded);
  };

  const formatDomainLinks = (domainLinks) => {
    return domainLinks.map((link, index) => (
      <div key={index}>
        <a href={`https://${link}`} target="_blank" rel="noopener noreferrer">
          {link}
        </a>
      </div>
    ));
  };

  const formatKeywords = (keywords) => {
    return (
      <div>
        {keywords.map((kw, index) => (
          <div key={index}>
            {kw[0]} - {kw[1]}
          </div>
        ))}
      </div>
    );
  };
  

  return (
    <StyledTable>
      <thead>
        <tr>
          <StyledTableHeader>Files</StyledTableHeader>
          <StyledTableHeader>Sentiment</StyledTableHeader>
          <StyledTableHeader>Keywords</StyledTableHeader>
          <StyledTableHeader>Score</StyledTableHeader>
          <StyledTableHeader>Upload Date</StyledTableHeader>
          <StyledTableHeader>Domain Links</StyledTableHeader>
        </tr>
      </thead>
      <tbody>
        <StyledTableRow onClick={toggleExpansion}>
          <StyledTableCell>{data.files.join(', ')}</StyledTableCell>
          <StyledTableCell>{data.sentiment}</StyledTableCell>
          <StyledTableCell>{formatKeywords(data.keywords)}</StyledTableCell>
          <StyledTableCell>{data.score.toFixed(2)}</StyledTableCell>
          <StyledTableCell>{data.upload_date}</StyledTableCell>
          <StyledTableCell>{formatDomainLinks(data.domain_links)}</StyledTableCell>
        </StyledTableRow>
        {expanded && (
          <StyledTableRow>
            <StyledTableCell colSpan="6">
              <div style={{ whiteSpace: 'pre-wrap' }}>
                Full Data: {JSON.stringify(data, null, 2)}
              </div>
            </StyledTableCell>
          </StyledTableRow>
        )}
      </tbody>
    </StyledTable>
  );
};

export default DataDisplayComponent;
