const App = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('/upload', {
        method: 'POST',
        body: formData,
      });

      const resultData = await response.json();
      setResult(resultData);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <>
      <GlobalStyles />
      <Nav>
        <Logo>Logo</Logo>
        <NavLinks id="desktop-nav">
          <NavLink href="#">Home</NavLink>
          <NavLink href="#">About</NavLink>
          <NavLink href="#">Contact</NavLink>
        </NavLinks>
        <HamburgerNav id="hamburger-nav">
          <HamburgerMenu className="hamburger-menu" onClick={toggleMenu}>
            <HamburgerIcon className={`hamburger-icon ${menuOpen ? 'open' : ''}`}>
              <span></span>
              <span></span>
              <span></span>
            </HamburgerIcon>
          </HamburgerMenu>
          <MenuLinks className={`menu-links ${menuOpen ? 'open' : ''}`}>
            <MenuList>
              <MenuItem href="#">Home</MenuItem>
              <MenuItem href="#">About</MenuItem>
              <MenuItem href="#">Contact</MenuItem>
            </MenuList>
          </MenuLinks>
        </HamburgerNav>
      </Nav>
      <Section>
        <SectionContainer>
          <SectionPicContainer></SectionPicContainer>
          <SectionText>
            <Title>Title</Title>
            <SectionTextP2>Text</SectionTextP2>
          </SectionText>
        </SectionContainer>
      </Section>
      <BtnContainer>
        <label htmlFor="fileInput">
          <Btn className="btn-color-1">Choose File</Btn>
          <input id="fileInput" type="file" onChange={handleFileChange} style={{ display: 'none' }} />
        </label>
        <Btn className="btn-color-1" onClick={handleUpload}>Upload</Btn>
      </BtnContainer>
      <AdditionalSection>
        {/* Your additional section components */}
      </AdditionalSection>
      <Contact>
        {/* Your contact components */}
      </Contact>
      {/* Display analysis results if available */}
      {result && (
        <Container className="innerContainer">
          <h2>Analysis Results:</h2>
          <p>Score: {result.score}</p>
        </Container>
      )}
    </>
  );
};

export default App;








