const AboutSection = styled.section`
/* Add your styles for the About section here */
padding: 20px;
background-color: #f8f8f8;


`;


const Home = styled.section`
/* Add your styles for the About section here */
padding: 20px;
background-color: #f8f8f8;
`;
const App = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('/upload', {
        method: 'POST',
        body: formData,
      });

      const resultData = await response.json();
      setResult(resultData);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <Router>
      <>
        <GlobalStyles />
        <Nav>
          <Logo>News Bias Analysis</Logo>
          <NavLinks id="desktop-nav">
            <Link to="/" smooth={true} duration={500}><MenuItem>Home</MenuItem></Link>
            <Link to="/about" smooth={true} duration={500}><MenuItem>About</MenuItem></Link>
            <Link to="/analysis" smooth={true} duration={500}><MenuItem>Analysis</MenuItem></Link>
            <Link to="/user" smooth={true} duration={500}><MenuItem>User</MenuItem></Link>
          </NavLinks>

          <HamburgerNav id="hamburger-nav">
            <HamburgerMenu className="hamburger-menu" onClick={toggleMenu}>
              <HamburgerIcon className={`hamburger-icon ${menuOpen ? 'open' : ''}`}>
                <span></span>
                <span></span>
                <span></span>
              </HamburgerIcon>
            </HamburgerMenu>
            <MenuLinks className={`menu-links ${menuOpen ? 'open' : ''}`}>
              <MenuList>
                <MenuItem href="#">Home</MenuItem>
                <MenuItem href="#">About</MenuItem>
                <MenuItem href="#">Analysis</MenuItem>
                <MenuItem href="#">User</MenuItem>
              </MenuList>
            </MenuLinks>
          </HamburgerNav>
        </Nav>
        <Route path="/" exact component={Home} />
        <Route path="/about" component={AboutSection} />
        <Route path="/analysis" component={Analysis} />
        <Route path="/user" component={LoginSignup} />
        <AboutSection id="about">
          <About>
            <About>
              <AboutPicContainer></AboutPicContainer>
              <AboutText>
                <Title>Upload Document</Title>
                <AboutTextP2>Receive the Best Bias</AboutTextP2>
              </AboutText>
            </About>
          </About>
        </AboutSection>
        <Analysis id="Analysis">
          <BtnContainer>
            <input type="file" onChange={handleFileChange} style={{ display: 'none' }} />
            <Btn className="btn-color-1" onClick={() => document.querySelector('input[type="file"]').click()}>Choose File</Btn>
            <Btn className="btn-color-1" onClick={handleUpload}>Upload</Btn>
          </BtnContainer>
          <AdditionalSection>
            {/* Your additional section components */}
          </AdditionalSection>
          {result && (
            <Container2 className="innerContainer">
              <h2>Analysis Results:</h2>
              <p>Score: {result.score}</p>
            </Container2>
          )}
        </Analysis>
        <User id="User">
          <Title></Title>
        </User>
        {/* Display analysis results if available */}
      </>
    </Router>
  );
};

export default App;




