import React, { useState } from 'react';
import { Button, Typography, Container, Paper, Input, InputLabel, FormControl, Grid, CircularProgress } from '@mui/material';

function App() {
  const [data, setData] = useState([{}]);
  const [url, setUrl] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = () => {
    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);

    fetch('/upload', {
      method: 'POST',
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        // Handle the response as needed
        setData(data); // Update state with the received data
      })
      .catch(error => console.error('There was an error uploading the file:', error))
      .finally(() => setLoading(false));
  };

  return (
    <Container>
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Crisis Events Analysis Tool
        </Typography>

        <FormControl fullWidth margin="normal">
          <InputLabel htmlFor="fileInput">Upload File</InputLabel>
          <Input
            id="fileInput"
            type="file"
            onChange={handleFileChange}
            accept=".json"
          />
        </FormControl>

        <Button
          variant="contained"
          color="primary"
          onClick={handleUpload}
          disabled={!file || loading}
          style={{ marginTop: '20px' }}
        >
          Upload
        </Button>

        {loading && <CircularProgress style={{ marginTop: '20px' }} />}

        {/* Display the uploaded data or other components as needed */}
        {data.length > 0 && (
          <Grid container spacing={2} style={{ marginTop: '20px' }}>
            {/* Render data components here */}
          </Grid>
        )}
      </Paper>
    </Container>
  );
}

export default App;




