import React, { useState } from 'react';
import styled from 'styled-components';

const ResultsTable = styled.table`
  width: 100%;
  border-collapse: collapse;
  table-layout: fixed; // This helps maintain equal column widths
`;

const ResultsTableHeader = styled.th`
  padding: 8px;
  text-align: left;
  background-color: #f2f2f2;
  word-wrap: break-word; // This ensures text breaks to the next line
`;

const ResultsTableRow = styled.tr`
  cursor: pointer;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const ResultsTableCell = styled.td`
  padding: 8px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
`;

const DataDisplayComponent = ({ dataArray }) => {
  const [expandedRows, setExpandedRows] = useState(new Set());

  const handleRowClick = (id) => {
    setExpandedRows((prevExpandedRows) => {
      const newExpandedRows = new Set(prevExpandedRows);
      if (newExpandedRows.has(id)) {
        newExpandedRows.delete(id);
      } else {
        newExpandedRows.add(id);
      }
      return newExpandedRows;
    });
  };

  const formatDomainLinks = (domainLinks) => {
    return Array.isArray(domainLinks) ? domainLinks.map((link, linkIndex) => (
      <div key={linkIndex}>
        <a href={link} target="_blank" rel="noopener noreferrer">
          {link}
        </a>
      </div>
    )) : <div><a href={domainLinks || '#'} target="_blank" rel="noopener noreferrer">{domainLinks || 'N/A'}</a></div>;
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Filename</th>
          <th>Sentiment</th>
          <th>Keywords</th>
          <th>Score</th>
          <th>Upload Date</th>
          <th>Domain Links</th>
        </tr>
      </thead>
      <tbody>
        {dataArray.map((item, index) => (
          <>
            <tr key={item._id} onClick={() => handleRowClick(item._id)}>
              <td>{item.filename}</td>
              <td>{item.sentiment}</td>
              <td>{Array.isArray(item.keywords) ? item.keywords.join(', ') : item.keywords || 'N/A'}</td>
              <td>{item.score}</td>
              <td>{item.upload_date}</td>
              <td>
                {formatDomainLinks(item.domain_links)}
              </td>
            </tr>
            {
              expandedRows.has(item._id) && (
                <tr key={`${item._id}-expanded`}>
                  <td colSpan="6">
                    {/* Render more details here */}
                    {/* For example: */}
                    <div>
                      Detailed analysis information or additional data...
                    </div>
                  </td>
                </tr>
              )
            }
          </>
        ))}
      </tbody>
    </table>
  );
};

export default DataDisplayComponent;
