import React, { useState } from 'react';
import styled from 'styled-components';

const StyledTable = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const StyledTableHeader = styled.th`
  background-color: #f9f9f9;
  border: 1px solid #ddd;
  padding: 8px;
  text-align: left;
`;

const StyledTableRow = styled.tr`
  cursor: pointer;
  &:hover {
    background-color: #f2f2f2;
  }
`;

const StyledTableCell = styled.td`
  border: 1px solid #ddd;
  padding: 8px;
  white-space: nowrap; /* Prevents content from wrapping */
  overflow: hidden; /* Hide overflowing content */
  text-overflow: ellipsis; /* Display ellipsis for overflowed content */
`;

const DataDisplayComponent = ({ dataArray }) => {
  const [expandedRows, setExpandedRows] = useState(new Set());

  const toggleRowExpansion = (id) => {
    setExpandedRows((prevExpandedRows) => {
      const newExpandedRows = new Set(prevExpandedRows);
      if (newExpandedRows.has(id)) {
        newExpandedRows.delete(id);
      } else {
        newExpandedRows.add(id);
      }
      return newExpandedRows;
    });
  };

  const formatDomainLinks = (domainLinks) => {
    return Array.isArray(domainLinks)
      ? domainLinks.map((link, linkIndex) => (
          <div key={linkIndex}>
            <a href={link} target="_blank" rel="noopener noreferrer">
              {link}
            </a>
          </div>
        ))
      : (
          <div>
            <a href={domainLinks || '#'} target="_blank" rel="noopener noreferrer">
              {domainLinks || 'N/A'}
            </a>
          </div>
        );
  };

  return (
    <StyledTable>
      <thead>
        <tr>
          <StyledTableHeader>Filename</StyledTableHeader>
          <StyledTableHeader>Sentiment</StyledTableHeader>
          <StyledTableHeader>Keywords</StyledTableHeader>
          <StyledTableHeader>Score</StyledTableHeader>
          <StyledTableHeader>Upload Date</StyledTableHeader>
          <StyledTableHeader>Domain Links</StyledTableHeader>
        </tr>
      </thead>
      <tbody>
        {dataArray.map((item) => (
          <React.Fragment key={item._id}>
            <StyledTableRow onClick={() => toggleRowExpansion(item._id)}>
              <StyledTableCell>{item.filename}</StyledTableCell>
              <StyledTableCell>{item.sentiment}</StyledTableCell>
              <StyledTableCell>{Array.isArray(item.keywords) ? item.keywords.join(', ') : item.keywords || 'N/A'}</StyledTableCell>
              <StyledTableCell>{item.score}</StyledTableCell>
              <StyledTableCell>{item.upload_date}</StyledTableCell>
              <StyledTableCell>
                {formatDomainLinks(item.domain_links)}
              </StyledTableCell>
            </StyledTableRow>
            {
              expandedRows.has(item._id) && (
                <StyledTableRow>
                  <StyledTableCell colSpan="6">
                    {/* Render the full content here */}
                    {/* For example: */}
                    <div>
                      Filename: {item.filename}<br />
                      Sentiment: {item.sentiment}<br />
                      Keywords: {Array.isArray(item.keywords) ? item.keywords.join(', ') : item.keywords || 'N/A'}<br />
                      Score: {item.score}<br />
                      Upload Date: {item.upload_date}<br />
                      Domain Links: {formatDomainLinks(item.domain_links)}
                    </div>
                  </StyledTableCell>
                </StyledTableRow>
              )
            }
          </React.Fragment>
        ))}
      </tbody>
    </StyledTable>
  );
};

export default DataDisplayComponent;
