const App = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('/upload', {
        method: 'POST',
        body: formData,
      });

      const resultData = await response.json();
      setResult(resultData);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <>
      <GlobalStyles />
      <Nav>
        <Logo>News Bias Analysis</Logo>
        <NavLinks id="desktop-nav">
          <NavLink href="#">Home</NavLink>
          <Link to="about" smooth={true} duration={500}><NavLink>About</NavLink></Link> {/* Use Link from react-scroll for smooth scrolling */}
          <NavLink href="#">Analysis</NavLink>
          <NavLink href="#">User</NavLink>
        </NavLinks>
        <HamburgerNav id="hamburger-nav">
          <HamburgerMenu className="hamburger-menu" onClick={toggleMenu}>
            <HamburgerIcon className={`hamburger-icon ${menuOpen ? 'open' : ''}`}>
              <span></span>
              <span></span>
              <span></span>
            </HamburgerIcon>
          </HamburgerMenu>
          <MenuLinks className={`menu-links ${menuOpen ? 'open' : ''}`}>
            <MenuList>
              <MenuItem href="#">Home</MenuItem>
              <Link to="about" smooth={true} duration={500}><MenuItem>About</MenuItem></Link> {/* Use Link from react-scroll for smooth scrolling */}
              <MenuItem href="#">Analysis</MenuItem>
              <MenuItem href="#">User</MenuItem>
            </MenuList>
          </MenuLinks>
        </HamburgerNav>
      </Nav>
      <AboutSection id="about">
        {/* Your About section components */}
      </AboutSection>
      <BtnContainer>
        <input type="file" onChange={handleFileChange} style={{ display: 'none' }} />
        <button className="btn-color-1" onClick={() => document.querySelector('input[type="file"]').click()}>Choose File</button>
        <button className="btn-color-1" onClick={handleUpload}>Upload</button>
      </BtnContainer>
      <Container2>
        {result && (
          <div>
            <h2>Analysis Results:</h2>
            <p>Score: {result.score}</p>
          </div>
        )}
      </Container2>
    </>
  );
};

